import React from "react";

const About = () => {
  return (
    <section id="about" className="py-20 bg-gray-50">
      <div className="max-w-4xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-8">About Me</h2>

        <div className="bg-white p-8 rounded-2xl shadow-lg text-gray-700 text-lg leading-relaxed">
          <p>
            I am a passionate <strong>Frontend Developer</strong> and an aspiring
            <strong> MERN Stack Developer</strong> with strong knowledge in
            React.js, JavaScript, HTML, CSS, and MySQL.
          </p>

          
        </div>
      </div>
    </section>
  );
};

export default About;
