import { motion } from "framer-motion";

const highlights = [
  {
    emoji: "âš¡",
    title: "Fast Learner",
    desc: "Quickly adapts to new technologies and tools."
  },
  {
    emoji: "ðŸŽ¨",
    title: "UI Focused",
    desc: "Builds clean, modern, and responsive interfaces."
  },
  {
    emoji: "ðŸ§ ",
    title: "Problem Solver",
    desc: "Enjoys breaking down problems into simple solutions."
  }
];

const About = () => {
  return (
    <section id="about" className="py-24 bg-gray-50 dark:bg-gray-900">
      <div className="max-w-5xl mx-auto px-6">

        {/* Main Glass Card */}
        <motion.div
          initial={{ opacity: 0, y: 60 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="backdrop-blur-xl bg-white/70 dark:bg-gray-800/70
          rounded-3xl shadow-2xl p-10"
        >
          <h2 className="text-4xl font-bold mb-6 text-center">
            About <span className="text-indigo-500">Me</span>
          </h2>

          <p className="text-lg text-gray-700 dark:text-gray-300 leading-relaxed text-center">
            I am a passionate <strong>Frontend Developer</strong> with strong
            skills in <strong>HTML, CSS, JavaScript, React, Java, and SQL</strong>.
            I enjoy crafting modern, responsive, and visually engaging web
            applications.
          </p>

          <p className="mt-4 text-lg text-gray-700 dark:text-gray-300 leading-relaxed text-center">
            My focus is on clean UI, smooth animations, and creating user-friendly
            experiences that feel professional and polished.
          </p>

          {/* Highlights Section */}
          <div className="mt-12 grid md:grid-cols-3 gap-6">
            {highlights.map((item, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.2 }}
                viewport={{ once: true }}
                whileHover={{ scale: 1.05 }}
                className="rounded-2xl p-6 text-center
                bg-white/60 dark:bg-gray-800/60
                shadow-lg hover:shadow-xl transition"
              >
                <div className="text-4xl mb-3">{item.emoji}</div>
                <h3 className="text-xl font-semibold mb-2">
                  {item.title}
                </h3>
                <p className="text-gray-600 dark:text-gray-400 text-sm">
                  {item.desc}
                </p>
              </motion.div>
            ))}
          </div>

        </motion.div>
      </div>
    </section>
  );
};

export default About;
