import React from "react";

const Skills = () => {
  const skillCategories = [
    {
      title: "Programming",
      skills: ["Python", "Java", "JavaScript"],
      gradient: "from-indigo-500 to-purple-500",
    },
    {
      title: "Web Technologies",
      skills: ["HTML", "CSS", "React.js"],
      gradient: "from-pink-500 to-purple-500",
    },
    {
      title: "Other Skills",
      skills: ["MySQL", "Team Collaboration"],
      gradient: "from-indigo-500 to-blue-500",
    },
  ];

  return (
    <section id="skills" className="py-20 bg-white dark:bg-gray-900 transition-colors duration-500">
      <div className="max-w-6xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-12">Skills</h2>

        <div className="grid md:grid-cols-3 gap-8">
          {skillCategories.map((category, i) => (
            <div
              key={i}
              className={`p-6 rounded-2xl shadow-lg text-white bg-gradient-to-r ${category.gradient} transform transition duration-500 hover:scale-105`}
              style={{ animation: `fadeInUp 0.5s ${i * 0.2}s forwards`, opacity: 0 }}
            >
              <h3 className="text-2xl font-semibold mb-4">{category.title}</h3>
              {category.skills.map((skill, index) => (
                <p key={index}>{skill}</p>
              ))}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
