import { motion } from "framer-motion";
import profile from "../assets/profile.jpg";

const Home = () => {
  return (
    <section
      id="home"
      className="
        min-h-screen flex items-center
        bg-gradient-to-br from-indigo-700 via-purple-600 to-pink-500
        pt-28
        relative overflow-hidden
      "
    >
      {/* Soft background glow */}
      <div className="absolute -top-32 -left-32 w-96 h-96 bg-pink-400/30 rounded-full blur-3xl"></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-indigo-400/30 rounded-full blur-3xl"></div>

      <div className="relative z-10 max-w-6xl mx-auto px-6 grid md:grid-cols-2 gap-14 items-center">

        {/* TEXT */}
        <motion.div
          initial={{ opacity: 0, x: -60 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="text-white"
        >
          <h1 className="text-5xl md:text-6xl font-extrabold leading-tight">
            Hi, Iâ€™m{" "}
            <span className="bg-gradient-to-r from-yellow-300 to-pink-300 bg-clip-text text-transparent">
              Angel Sriram
            </span>{" "}
            ðŸ‘‹
          </h1>

          <p className="mt-6 text-xl md:text-2xl opacity-95">
            Frontend Developer | MERN Stack Enthusiast
          </p>

          <p className="mt-5 text-lg opacity-85 max-w-md">
            I craft modern, responsive, and visually elegant web experiences
            with clean UI, smooth animations, and attention to detail.
          </p>

          <motion.a
            whileHover={{ scale: 1.08 }}
            whileTap={{ scale: 0.95 }}
            href="/assets/resume.pdf"
            download
            className="
              inline-block mt-10 px-10 py-4 rounded-xl
              bg-white text-indigo-700 font-semibold
              shadow-xl hover:bg-gray-100 transition
            "
          >
            Download Resume
          </motion.a>
        </motion.div>

        {/* IMAGE */}
        <motion.div
          initial={{ opacity: 0, x: 60 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="flex justify-center"
        >
          <motion.div
            animate={{ y: [0, -15, 0] }}
            transition={{ duration: 4, repeat: Infinity }}
            className="relative"
          >
            <img
              src={profile}
              alt="Angel Sriram"
              className="
                w-64 h-64 md:w-80 md:h-80
                object-cover rounded-full
                border-4 border-white
                shadow-2xl
              "
            />
            <div className="absolute inset-0 rounded-full border-4 border-white/30 blur-sm"></div>
          </motion.div>
        </motion.div>

      </div>
    </section>
  );
};

export default Home;
