import { motion } from "framer-motion";

const About = () => {
  return (
    <section id="about" className="py-20 bg-gray-50">
      <div className="max-w-4xl mx-auto px-6 text-center">
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-8"
        >
          About Me
        </motion.h2>

        <motion.div
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="bg-white p-8 rounded-2xl shadow-lg text-gray-700 text-lg leading-relaxed"
        >
          <p>
            I am a passionate <strong>Frontend Developer</strong> and aspiring
            <strong> MERN Stack Developer</strong> with strong knowledge in
            React.js, JavaScript, HTML, CSS, and MySQL.
          </p>

          <p className="mt-4">
            I enjoy building modern, responsive, and user-friendly web
            applications with clean UI and smooth animations.
          </p>

          <p className="mt-4">
            I am highly motivated to learn new technologies and grow as a
            professional developer.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
