import { motion } from "framer-motion";
import profile from "../assets/profile.jpg";

const Home = () => {
  return (
    <section
      id="home"
      className="min-h-screen flex items-center justify-center
      bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500
      relative overflow-hidden"
    >
      {/* Soft Glow Background */}
      <div className="absolute -top-20 -left-20 w-96 h-96 bg-pink-400/30 rounded-full blur-3xl"></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-indigo-400/30 rounded-full blur-3xl"></div>

      <div className="max-w-6xl w-full px-6 grid md:grid-cols-2 gap-12 items-center relative z-10">

        {/* LEFT CONTENT */}
        <motion.div
          initial={{ opacity: 0, x: -60 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="text-white"
        >
          <h1 className="text-5xl md:text-6xl font-extrabold leading-tight">
            Hi, Iâ€™m{" "}
            <span className="text-yellow-300">Angel</span>{" "}
            <span className="inline-block">ðŸ‘‹</span>
          </h1>

          <p className="mt-6 text-xl md:text-2xl opacity-90">
            Frontend Developer | MERN Stack Enthusiast
          </p>

          <p className="mt-4 text-lg opacity-80 max-w-md">
            I design and build modern, responsive web experiences with clean UI,
            smooth animations, and strong attention to detail.
          </p>

          {/* BUTTON */}
          <motion.a
            whileHover={{
              scale: 1.1,
              boxShadow: "0px 0px 25px rgba(255,255,255,0.6)",
            }}
            whileTap={{ scale: 0.95 }}
            href="/assets/resume.pdf"
            download
            className="inline-block mt-8 px-10 py-4
            bg-white text-indigo-700 font-semibold rounded-xl
            shadow-xl transition"
          >
            Download Resume
          </motion.a>
        </motion.div>

        {/* RIGHT IMAGE */}
        <motion.div
          initial={{ opacity: 0, x: 60 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="flex justify-center"
        >
          <motion.div
            animate={{ y: [0, -15, 0] }}
            transition={{ duration: 4, repeat: Infinity }}
            className="relative"
          >
            <img
              src={profile}
              alt="Angel Profile"
              className="w-64 h-64 md:w-80 md:h-80 object-cover
              rounded-full border-4 border-white shadow-2xl"
            />

            {/* Ring Glow */}
            <div className="absolute inset-0 rounded-full
            border-4 border-white/30 blur-sm"></div>
          </motion.div>
        </motion.div>

      </div>
    </section>
  );
};

export default Home;
