import { motion } from "framer-motion";
import {
  FaJava,
  FaHtml5,
  FaCss3Alt,
  FaJs,
  FaReact,
  FaDatabase,
} from "react-icons/fa";

const skills = [
  { name: "Java", icon: <FaJava className="text-red-500" /> },
  { name: "HTML", icon: <FaHtml5 className="text-orange-500" /> },
  { name: "CSS", icon: <FaCss3Alt className="text-blue-500" /> },
  { name: "JavaScript", icon: <FaJs className="text-yellow-400" /> },
  { name: "React", icon: <FaReact className="text-cyan-400" /> },
  { name: "SQL", icon: <FaDatabase className="text-purple-500" /> },
];

const Skills = () => {
  return (
    <section id="skills" className="py-24 bg-gradient-to-b from-white to-gray-100">
      <div className="max-w-6xl mx-auto px-6">

        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold text-center mb-14"
        >
           <span className="text-indigo-600">Skills</span>
        </motion.h2>

        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-3 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0.85 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ delay: index * 0.12 }}
              whileHover={{ scale: 1.12 }}
              className="bg-white/70 backdrop-blur-xl
                border border-gray-200 rounded-2xl
                shadow-lg hover:shadow-2xl
                p-8 flex flex-col items-center
                transition-all duration-300"
            >
              <div className="text-5xl mb-4">{skill.icon}</div>
              <p className="text-xl font-semibold">{skill.name}</p>
            </motion.div>
          ))}
        </div>

      </div>
    </section>
  );
};

export default Skills;
