import { motion } from "framer-motion";

export default function About() {
  return (
    <section id="about" className="py-24 bg-gray-50">
      <div className="max-w-5xl mx-auto px-6 text-center">

        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-10"
        >
          About Me
        </motion.h2>

        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6 }}
          className="bg-white p-10 rounded-3xl shadow-xl text-gray-700 text-lg leading-relaxed"
        >
          <p>
            I am a passionate <strong>Frontend Developer</strong> and aspiring
            <strong> MERN Stack Developer</strong> with strong expertise in
            React.js, JavaScript, HTML, CSS, and MySQL.
          </p>

          <p className="mt-5">
            I enjoy crafting modern, responsive, and visually appealing web
            applications with smooth animations and clean UI/UX.
          </p>

          <p className="mt-5">
            I am continuously learning new technologies and aim to contribute
            to impactful projects in a professional development environment.
          </p>
        </motion.div>

      </div>
    </section>
  );
}
