import { motion } from "framer-motion";
import {
  FaJava,
  FaHtml5,
  FaCss3Alt,
  FaJs,
  FaReact,
  FaDatabase,
} from "react-icons/fa";

const skills = [
  { name: "HTML / CSS", percent: 90, icon: <FaHtml5 /> },
  { name: "Java", percent: 85, icon: <FaJava /> },
  { name: "SQL", percent: 80, icon: <FaDatabase /> },
  { name: "JavaScript", percent: 70, icon: <FaJs /> },
  { name: "React", percent: 70, icon: <FaReact /> },
];

const Skills = () => {
  return (
    <section
      id="skills"
      className="py-24 bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50"
    >
      <div className="max-w-5xl mx-auto px-6">

        {/* Heading */}
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold text-center mb-14"
        >
          <span className="text-indigo-600">Skills</span>
        </motion.h2>

        {/* ðŸ”¹ MOBILE VIEW */}
        <div className="block md:hidden space-y-6">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-white rounded-2xl p-6 shadow-lg"
            >
              <div className="flex justify-between items-center mb-2">
                <div className="flex items-center gap-3 text-lg font-semibold">
                  <span className="text-indigo-600 text-xl">
                    {skill.icon}
                  </span>
                  {skill.name}
                </div>
                <span className="text-sm font-medium">
                  {skill.percent}%
                </span>
              </div>

              {/* Progress Bar */}
              <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden">
                <motion.div
                  initial={{ width: 0 }}
                  whileInView={{ width: `${skill.percent}%` }}
                  transition={{ duration: 1 }}
                  className="h-full bg-indigo-500 rounded-full"
                />
              </div>
            </motion.div>
          ))}
        </div>

        {/* ðŸ”¹ DESKTOP VIEW (YOUR ORIGINAL STYLE) */}
        <div className="hidden md:grid grid-cols-3 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.1 }}
              className="bg-white/70 backdrop-blur-xl rounded-2xl p-8
              shadow-lg flex flex-col items-center"
            >
              <div className="text-5xl mb-4 text-indigo-600">
                {skill.icon}
              </div>
              <p className="text-xl font-semibold">{skill.name}</p>
            </motion.div>
          ))}
        </div>

      </div>
    </section>
  );
};

export default Skills;
