import { motion } from "framer-motion";
import profile from "../assets/profile.jpg";

const Home = () => {
  return (
    <section
      id="home"
      className="min-h-screen flex items-center justify-center
      bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500"
    >
      <div className="max-w-6xl w-full px-6 grid md:grid-cols-2 gap-10 items-center">

        {/* TEXT */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="text-white"
        >
          <h1 className="text-5xl md:text-6xl font-extrabold mb-4">
            Hi, I'm <span className="text-yellow-300">Angel</span> ðŸ‘‹
          </h1>

          <p className="text-xl md:text-2xl mb-6 opacity-90">
            Frontend Developer | MERN Stack Enthusiast
          </p>

          <motion.a
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            href="/resume.pdf"
            download
            className="inline-block px-8 py-4 bg-white text-indigo-700
                       font-semibold rounded-xl shadow-xl hover:shadow-2xl transition"
          >
            Download Resume
          </motion.a>
        </motion.div>

        {/* IMAGE */}
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
          className="flex justify-center"
        >
          <img
            src={profile}
            alt="Angel Profile"
            className="w-64 h-64 md:w-80 md:h-80 object-cover
                       rounded-full border-4 border-white shadow-2xl"
          />
        </motion.div>

      </div>
    </section>
  );
};

export default Home;
