import { motion } from "framer-motion";

const skills = [
  {
    title: "Programming",
    items: ["JavaScript", "Java", "Python"],
    gradient: "from-indigo-500 to-purple-500",
  },
  {
    title: "Web Technologies",
    items: ["HTML", "CSS", "React.js", "Tailwind CSS"],
    gradient: "from-pink-500 to-purple-500",
  },
  {
    title: "Tools & Database",
    items: ["MySQL", "Git & GitHub", "VS Code"],
    gradient: "from-indigo-500 to-blue-500",
  },
];

export default function Skills() {
  return (
    <section id="skills" className="py-24 bg-white">
      <div className="max-w-6xl mx-auto px-6 text-center">

        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-14"
        >
          Skills
        </motion.h2>

        <div className="grid md:grid-cols-3 gap-10">
          {skills.map((skill, i) => (
            <motion.div
              key={i}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.2 }}
              whileHover={{ scale: 1.08 }}
              className={`p-8 rounded-3xl shadow-xl bg-gradient-to-r ${skill.gradient} text-white`}
            >
              <h3 className="text-2xl font-semibold mb-4">{skill.title}</h3>
              {skill.items.map((item, idx) => (
                <p key={idx} className="opacity-90">{item}</p>
              ))}
            </motion.div>
          ))}
        </div>

      </div>
    </section>
  );
}
