import { motion } from "framer-motion";

const projects = [
  {
    title: "Portfolio Website",
    desc: "A modern responsive portfolio built with React, Tailwind CSS and Framer Motion animations.",
  },
  {
    title: "Personal Finance Tracker",
    desc: "Tracks expenses, alerts overspending, and provides monthly and yearly summaries.",
  },
];

export default function Projects() {
  return (
    <section id="projects" className="py-24 bg-gray-50">
      <div className="max-w-6xl mx-auto px-6 text-center">

        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-14"
        >
          Projects
        </motion.h2>

        <div className="grid md:grid-cols-2 gap-10">
          {projects.map((project, i) => (
            <motion.div
              key={i}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.2 }}
              whileHover={{ scale: 1.05 }}
              className="bg-white p-8 rounded-3xl shadow-xl text-left"
            >
              <h3 className="text-2xl font-semibold mb-3">
                {project.title}
              </h3>
              <p className="text-gray-600">{project.desc}</p>
            </motion.div>
          ))}
        </div>

      </div>
    </section>
  );
}
