import { motion } from "framer-motion";

const projects = [
  {
    title: "Portfolio Website",
    description:
      "A responsive personal portfolio built with React and Tailwind CSS featuring animations and modern UI.",
  },
  {
    title: "Personal Finance Tracker",
    description:
      "Tracks expenses, alerts overspending, and provides analytics with a clean dashboard.",
  },
];

const Projects = () => {
  return (
    <section id="projects" className="py-24 bg-gray-50 dark:bg-gray-900">
      <div className="max-w-6xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-14">
          My <span className="text-indigo-500">Projects</span>
        </h2>

        <div className="grid md:grid-cols-2 gap-10">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 60 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.05 }}
              className="relative group rounded-3xl shadow-2xl p-8
              bg-white dark:bg-gray-800"
            >
              <h3 className="text-2xl font-semibold mb-3">
                {project.title}
              </h3>

              <p className="text-gray-600 dark:text-gray-300">
                {project.description}
              </p>

              <div
                className="absolute inset-0 bg-indigo-600/90 text-white
                rounded-3xl flex items-center justify-center opacity-0
                group-hover:opacity-100 transition"
              >
                <p className="font-semibold">View on GitHub</p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
