import { motion } from "framer-motion";
import {
  FaHtml5,
  FaCss3Alt,
  FaJs,
  FaReact,
  FaGitAlt,
  FaGithub,
  FaDatabase,
} from "react-icons/fa";
import { SiMysql, SiTailwindcss } from "react-icons/si";

const skills = [
  { name: "HTML5", icon: <FaHtml5 />, color: "text-orange-500" },
  { name: "CSS3", icon: <FaCss3Alt />, color: "text-blue-500" },
  { name: "JavaScript", icon: <FaJs />, color: "text-yellow-400" },
  { name: "React.js", icon: <FaReact />, color: "text-cyan-400" },
  { name: "Tailwind CSS", icon: <SiTailwindcss />, color: "text-sky-400" },
  { name: "MySQL", icon: <SiMysql />, color: "text-blue-600" },
  { name: "Git", icon: <FaGitAlt />, color: "text-red-500" },
  { name: "GitHub", icon: <FaGithub />, color: "text-gray-800 dark:text-white" },
  { name: "Database", icon: <FaDatabase />, color: "text-purple-500" },
];

const Skills = () => {
  return (
    <section
      id="skills"
      className="py-20 bg-gradient-to-b from-white to-gray-100 dark:from-gray-900 dark:to-gray-800"
    >
      <div className="max-w-6xl mx-auto px-6">
        
        {/* Heading */}
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-4xl font-extrabold text-center mb-14"
        >
          My <span className="text-indigo-600">Skills</span>
        </motion.h2>

        {/* Skills Grid */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-8">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0.8 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ delay: index * 0.1, duration: 0.4 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.08 }}
              className="backdrop-blur-xl bg-white/40 dark:bg-white/10
              border border-white/20 dark:border-white/10
              rounded-2xl shadow-lg hover:shadow-2xl
              p-6 flex flex-col items-center justify-center
              transition-all duration-300 cursor-pointer"
            >
              <div className={`text-5xl mb-4 ${skill.color}`}>
                {skill.icon}
              </div>
              <p className="text-lg font-semibold text-gray-800 dark:text-white">
                {skill.name}
              </p>
            </motion.div>
          ))}
        </div>

      </div>
    </section>
  );
};

export default Skills;
