import { motion } from "framer-motion";

const features = [
  {
    icon: "âš¡",
    title: "Fast Learner",
    desc: "Quickly adapt to new technologies and tools",
  },
  {
    icon: "ðŸŽ¨",
    title: "UI Focused",
    desc: "Love crafting clean and modern user interfaces",
  },
  {
    icon: "ðŸ§ ",
    title: "Problem Solver",
    desc: "Enjoy solving real-world problems with code",
  },
];

const About = () => {
  return (
    <section
      id="about"
      className="py-24 bg-gray-50 dark:bg-gray-900"
    >
      <div className="max-w-5xl mx-auto px-6 text-center">
        
        {/* Glass Card */}
        <motion.div
          initial={{ opacity: 0, y: 60 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="backdrop-blur-xl bg-white/60 dark:bg-gray-800/60
          rounded-3xl shadow-2xl p-10"
        >
          <h2 className="text-4xl font-bold mb-6">
            About <span className="text-indigo-500">Me</span>
          </h2>

          <p className="text-lg text-gray-700 dark:text-gray-300 leading-relaxed">
            Iâ€™m a passionate <strong>Frontend Developer</strong> and aspiring
            <strong> MERN Stack Developer</strong> with strong skills in
            React.js, JavaScript, HTML, CSS, Java, and SQL.
          </p>

          <p className="mt-4 text-lg text-gray-700 dark:text-gray-300 leading-relaxed">
            I focus on building modern, responsive, and visually appealing
            interfaces with smooth animations and clean UI/UX.
          </p>

          {/* Feature Row */}
          <motion.div
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            variants={{
              visible: {
                transition: { staggerChildren: 0.2 },
              },
            }}
            className="mt-12 grid md:grid-cols-3 gap-8"
          >
            {features.map((item, index) => (
              <motion.div
                key={index}
                variants={{
                  hidden: { opacity: 0, y: 30 },
                  visible: { opacity: 1, y: 0 },
                }}
                className="flex flex-col items-center"
              >
                <div className="text-4xl mb-3">{item.icon}</div>
                <h3 className="font-semibold text-lg">{item.title}</h3>
                <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
                  {item.desc}
                </p>
              </motion.div>
            ))}
          </motion.div>

        </motion.div>
      </div>
    </section>
  );
};

export default About;
