import { motion } from "framer-motion";
import { Mail, Github, Linkedin, Phone } from "lucide-react";

const Contact = () => {
  return (
    <section id="contact" className="py-24 bg-white dark:bg-gray-950">
      <div className="max-w-5xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-14">
          Get <span className="text-indigo-500">In Touch</span>
        </h2>

        <div className="grid md:grid-cols-2 gap-8">
          {[
            { icon: Mail, text: "angel@email.com" },
            { icon: Phone, text: "+91 XXXXX XXXXX" },
            { icon: Linkedin, text: "LinkedIn Profile" },
            { icon: Github, text: "GitHub Profile" },
          ].map((item, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.08 }}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.15 }}
              viewport={{ once: true }}
              className="flex items-center justify-center gap-4
              p-6 rounded-2xl shadow-xl
              backdrop-blur-lg bg-white/60 dark:bg-gray-800/60"
            >
              <item.icon className="text-indigo-500" size={28} />
              <span className="text-lg font-medium">{item.text}</span>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Contact;
