import { motion } from "framer-motion";

const About = () => {
  return (
    <section id="about" className="py-20 bg-gray-100">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="max-w-4xl mx-auto px-6"
      >
        <h2 className="text-4xl font-bold text-center mb-8">
          About Me
        </h2>

        <div className="bg-white/70 backdrop-blur-lg shadow-xl rounded-2xl p-8 text-lg text-gray-700 leading-relaxed">
          I am a passionate MERN Stack Developer with strong knowledge in
          React.js, Node.js, Express, and MongoDB. I enjoy building modern,
          responsive, and user-friendly web applications with clean UI and
          smooth animations.
        </div>
      </motion.div>
    </section>
  );
};

export default About;
