import React from "react";

const Projects = () => {
  const projects = [
    {
      title: "Portfolio Website",
      desc: "A responsive personal portfolio website built using React.js and Tailwind CSS with smooth animations and modern UI design.",
      gradient: "from-indigo-500 to-purple-500",
    },
    {
      title: "All-in-One Personal Finance Tracker",
      desc: "A web application that tracks monthly and yearly expenses, sends alerts when spending exceeds limits, and provides daily reminders for expense logging.",
      gradient: "from-pink-500 to-yellow-500",
    },
  ];

  return (
    <section id="projects" className="py-20 bg-gray-50 dark:bg-gray-800 transition-colors duration-500">
      <div className="max-w-6xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-12">Projects</h2>
        <div className="grid md:grid-cols-2 gap-8">
          {projects.map((project, i) => (
            <div
              key={i}
              className={`bg-gradient-to-r ${project.gradient} p-6 rounded-2xl shadow-lg hover:shadow-xl transform transition duration-500 hover:scale-105`}
              style={{ animation: `fadeInUp 0.5s ${i * 0.2}s forwards`, opacity: 0 }}
            >
              <h3 className="text-2xl font-semibold mb-2 text-white">{project.title}</h3>
              <p className="text-white">{project.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
