import { motion } from "framer-motion";

const skills = [
  { title: "Programming", items: ["JavaScript", "Python", "Java"] },
  { title: "Frontend", items: ["React.js", "HTML", "CSS", "Tailwind CSS"] },
  { title: "Tools", items: ["Git", "GitHub", "MySQL"] },
];

const Skills = () => {
  return (
    <section id="skills" className="py-24 bg-white dark:bg-gray-950">
      <div className="max-w-6xl mx-auto px-6 text-center">
        <h2 className="text-4xl font-bold mb-14">
          My <span className="text-indigo-500">Skills</span>
        </h2>

        <div className="grid md:grid-cols-3 gap-10">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.08 }}
              className="rounded-3xl p-8 shadow-xl
              bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500
              text-white"
            >
              <h3 className="text-2xl font-semibold mb-4">{skill.title}</h3>
              {skill.items.map((item, i) => (
                <p key={i} className="opacity-90">{item}</p>
              ))}
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
