import React, { useState, useEffect } from "react";
import About from "./About";
import Skills from "./Skills";
import Projects from "./Projects";
import Contact from "./Contact";
import ParticlesBg from "particles-bg";
import { ArrowUp } from "lucide-react";

const App = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [showScroll, setShowScroll] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 300) setShowScroll(true);
      else setShowScroll(false);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors duration-500">
        {/* Hero Section with Particles */}
        <section className="relative h-screen flex flex-col justify-center items-center text-center overflow-hidden">
          <ParticlesBg type="cobweb" bg={true} />
          <h1 className="text-5xl md:text-6xl font-bold mb-4 z-10">
            Angel Sriram
          </h1>
          <p className="text-xl md:text-2xl mb-8 z-10">
            MERN Stack Developer | Full-Stack Web Applications
          </p>
          <button
            onClick={toggleDarkMode}
            className="px-6 py-3 bg-indigo-600 text-white rounded-full hover:bg-indigo-500 transition z-10"
          >
            Toggle {darkMode ? "Light" : "Dark"} Mode
          </button>
        </section>

        {/* About Section */}
        <About />

        {/* Skills Section */}
        <Skills />

        {/* Projects Section */}
        <Projects />

        {/* Contact Section */}
        <Contact />

        {/* Scroll-to-Top Button */}
        {showScroll && (
          <button
            onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}
            className="fixed bottom-6 right-6 p-4 bg-indigo-600 text-white rounded-full shadow-lg hover:bg-indigo-500 transition z-50"
          >
            <ArrowUp size={24} />
          </button>
        )}
      </div>
    </div>
  );
};

export default App;
