import { motion } from "framer-motion";
import { Mail, Phone, Github, Linkedin } from "lucide-react";

export default function Contact() {
  return (
    <section id="contact" className="py-24 bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500">
      <div className="max-w-6xl mx-auto px-6 text-center text-white">

        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold mb-14"
        >
          Contact Me
        </motion.h2>

        <div className="grid md:grid-cols-4 gap-8">
          
          {[{
            icon: <Mail size={32} />,
            label: "Email",
            value: "yourmail@gmail.com",
          },{
            icon: <Phone size={32} />,
            label: "Phone",
            value: "+91 XXXXX XXXXX",
          },{
            icon: <Github size={32} />,
            label: "GitHub",
            value: "github.com/yourprofile",
          },{
            icon: <Linkedin size={32} />,
            label: "LinkedIn",
            value: "linkedin.com/in/yourprofile",
          }].map((item, i) => (
            <motion.div
              key={i}
              whileHover={{ scale: 1.1 }}
              className="bg-white/15 backdrop-blur-lg p-6 rounded-3xl shadow-xl"
            >
              <div className="mb-3 text-yellow-300 flex justify-center">
                {item.icon}
              </div>
              <h3 className="font-semibold">{item.label}</h3>
              <p className="opacity-90 text-sm">{item.value}</p>
            </motion.div>
          ))}

        </div>
      </div>
    </section>
  );
}
